import React, { Component } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

class MultipleItems extends Component {
  render() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 100,
      slidesToShow: 3,
      slidesToScroll: 3
    };
    return (
        <div class="bg-black">
        <div class="robotics p-6">
         <h1 class="align-center mb-2">Latest News</h1>
         <div class="slide-container swiper">
                        <div class="slide-content">
                            <div class="card-wrapper swiper-wrapper"></div>
        {/* <Slider {...settings}> */}
        <div class="card swiper-slide">
            <div class="image-content" >
                <span class="overlay"></span>
                <div class="card-image rob-1">
                    <img src={this.props.Image}/>
                </div>
            </div>
            <div class="card-content">
                <h2 class="name">{this.props.title}</h2>
                <p class="description">{this.props.description}</p>
                <button class="btn btn-success">{this.props.btn}</button>
            </div>
        </div>
        {/* </Slider> */}
        
      </div>
      </div>
      </div>
      </div>
    );
  }
}
export default MultipleItems
